package main

import (
	"testing"
)

func TestRobloxSession_GetAuthTicket(t *testing.T) {
	session := NewRobloxSession("cirfu3120", "devlogin38291")
	//add your 2captcha key
	if err := session.Login("123"); err != nil {
		t.Error(err)
		t.FailNow()
	}

	ticket, err := session.GetAuthTicket(2965723516)
	if err != nil {
		t.Error(err)
		t.FailNow()
	}

	t.Log(ticket)
}

func TestGetAuthTicketWithCookie(t *testing.T) {
	ticket, err := GetAuthTicketWithCookie("_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_", 2965723516)
	if err != nil {
		t.Error(err)
		t.FailNow()
	}

	t.Log(ticket)
}
